// Morning wake time: first kitchen occupancy each day
import "timezone"

kitchen_wake = from(bucket: "home-assistant")
  |> range(start: -30d)
  |> filter(fn: (r) => r["entity_id"] == "binary_sensor.presence_kitchen")
  |> filter(fn: (r) => r["_field"] == "state")
  |> filter(fn: (r) => r["_value"] == "on")
  |> aggregateWindow(every: 1d, fn: first, createEmpty: false)
  |> map(fn: (r) => {
      // Convert to Europe/Warsaw timezone
      local_time = timezone.time(t: r._time, location: timezone.fixed(offset: 1h))
      hours = uint(v: date.hour(t: local_time))
      minutes = uint(v: date.minute(t: local_time))
      // Store as nanoseconds since midnight for graph
      return {r with
        _value: (hours * 3600000000000) + (minutes * 60000000000),
        _field: "Wake Time (Kitchen)"
      }
  })
  |> yield(name: "wake")

// Evening sleep time: last living room occupancy transition to off
import "timezone"

livingroom_sleep = from(bucket: "home-assistant")
  |> range(start: -30d)
  |> filter(fn: (r) => r["entity_id"] == "binary_sensor.presence_livingroom")
  |> filter(fn: (r) => r["_field"] == "state")
  |> filter(fn: (r) => r["_value"] == "off")
  |> aggregateWindow(every: 1d, fn: last, createEmpty: false)
  |> map(fn: (r) => {
      // Convert to Europe/Warsaw timezone
      local_time = timezone.time(t: r._time, location: timezone.fixed(offset: 1h))
      hours = uint(v: date.hour(t: local_time))
      minutes = uint(v: date.minute(t: local_time))
      // Store as nanoseconds since midnight for graph
      return {r with
        _value: (hours * 3600000000000) + (minutes * 60000000000),
        _field: "Sleep Time (Living Room)"
      }
  })
  |> yield(name: "sleep")
